file_frutti_path = "/Users/francescolucarelli/universitaÌ€/Primo anno 24-25/Primo Semestre/FDP/python/frutti.txt"# Scrivere i frutti nel filefrutta_agg = ["Fragola", "Ananas"]frutti = ["Mela", "Banana", "Arancia", "Kiwi", "Uva"]# frutti = frutti.append(frutta_agg)with open(file_frutti_path, encoding="utf8", mode="w") as FOUT:    for frutto in frutti:        FOUT.write(str(frutto.upper() + "\n"))        with open(file_frutti_path, encoding="utf8", mode="a") as FOUT:    for frutto in frutta_agg:        FOUT.write(str(frutto.upper() + "\n"))    # Leggere il contenuto del filewith open(file_frutti_path, encoding="utf8", mode="r") as FIN:    counter = 0    for line in FIN:        counter +=1        print(f"{counter}: {line.strip()}")  # Stampa ogni linea, rimuovendo eventuali spazi bianchi        